import React, { useState, useEffect } from 'react'; 
import userProfile from './Images/user-profile.png';
import { LoginForm } from './LoginForm';

export const HomePage: React.FC = () => {
  const [isFormOpen, setIsFormOpen] = useState<boolean>(false);
  const [userInfo, setUserInfo] = useState<{ username: string; password: string }>({ username: "", password: "" });
  const [remember, setRemember] = useState<boolean>(false);
  const [isLoggedIn, setIsLoggedIn] = useState<boolean>(false);
  const [formTitle, setFormTitle] = useState<string>("Create Account");
  const [db, setDb] = useState<IDBDatabase | null>(null); // Store db instance

  useEffect(() => {
    const indexedDB = window.indexedDB;
    const request = indexedDB.open("UserDatabase", 1);

    request.onerror = (event) => {
      console.error("Error accessing user database!", event);
    };

    request.onupgradeneeded = () => {
      const db = request.result;
      const store = db.createObjectStore("users", { autoIncrement: true });
      store.createIndex("username_and_password", ["username", "password"], { unique: true });
    };    

    request.onsuccess = () => {
      setDb(request.result); // Set the database instance in state
    };
  }, []); // Empty dependency array to only run once

  const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
  
    if (!userInfo.username || !userInfo.password) {
      console.error("Username and password must be provided.");
      return;
    }
  
    if (!db) {
      console.error("Database not initialized");
      return;
    }
  
    try {
      const userExists = await checkUserExists(userInfo.username, userInfo.password);
      if (userExists) {
        setFormTitle("Log In");
        setIsLoggedIn(true);
      } else {
        addUser(userInfo.username, userInfo.password);
        setFormTitle("Create Account");
        setIsLoggedIn(true);
      }
    } catch (error) {
      console.error("Error handling submit:", error);
    }
  };
  
  // Helper function to check if user exists
  const checkUserExists = (username: string, password: string): Promise<boolean> => {
    return new Promise((resolve, reject) => {
      const transaction = db!.transaction("users", "readonly");
      const store = transaction.objectStore("users");
      const userQuery = store.get([username, password]);
  
      userQuery.onsuccess = () => {
        resolve(!!userQuery.result);
      };
  
      userQuery.onerror = () => {
        reject("Error querying user data");
      };
    });
  };
  
  // Helper function to add a new user
  const addUser = (username: string, password: string) => {
    if (!db) {
      console.error("Database not initialized");
      return;
    }
  
    const transaction = db.transaction("users", "readwrite");
    const store = transaction.objectStore("users");
  
    // Ensure the correct structure: provide an object with fields matching the store's schema
    const newUser = { username, password };
    store.put(newUser);
  };
  
  

  const toggleForm = () => {
    setIsFormOpen(!isFormOpen);
  };

  const updateStatus = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    setUserInfo((prevInfo) => ({
      ...prevInfo,
      [name]: value,
    }));
  };

  const handleRemember = () => {
    setRemember(!remember);
  };

  return (
    <div>
      {isLoggedIn ? (
        <div style={{ position: "absolute", float: "left" }}>
          <h3>Signed in as: {userInfo.username}!</h3>
        </div>
      ) : (
        <div>
          <img
            src={userProfile}
            alt="User Profile"
            style={{ float: "left", width: '50px', height: '55px', cursor: 'pointer' }}
            onClick={toggleForm}
          />
        </div>
      )}
      <a href="https://bleaky11.github.io/starter_helpi/" style={{ color: 'black' }}>
        <h1>The Career Quiz</h1>
      </a>

      {isFormOpen && !isLoggedIn && (
        <LoginForm
          userInfo={userInfo}
          setUserInfo={setUserInfo}
          remember={remember}
          setRemember={setRemember}
          handleRemember={handleRemember}
          handleSubmit={handleSubmit}
          updateStatus={updateStatus}
          closeForm={() => setIsFormOpen(false)}
          formTitle={formTitle}
        />
      )}
    </div>
  );
};
