import React from 'react';
import {Form} from 'react-bootstrap'
import { useState } from 'react';
import './LoginForm.css';

interface LoginFormProps {
  closeForm: () => void;
}

interface userProfile
{
  email: string
  password: string
}

export const LoginForm: React.FC<LoginFormProps> = ({ closeForm }) => 
{
  const [userInfo, setInfo] = useState<userProfile>({email: "", password: ""});
  const [remember, setRemember] = useState<boolean>(false);

  function updateStatus(event: React.ChangeEvent<HTMLInputElement>)
  {
      const { name, value } = event.target;
      if(remember)
      {
        setInfo((prevInfo) => ({
          ...prevInfo,
          [name]: value, // Update the field based on the name attribute
        }));
      }
  }
  console.log(userInfo.email, userInfo.password);

  function handleRemember()
  {
    setRemember(!remember);
  }
  console.log(remember);

  return (
    <div className="form-popup" id="myForm">
      <form action="/action_page.php" className="form-container">
        <h1>Login</h1>
        <label htmlFor="email"><b>Email</b></label>
        <input type="text" value = {userInfo.email} placeholder="Enter Email" name="email" onChange={updateStatus} required />
        <label htmlFor="psw"><b>Password</b></label>
        <input type="password" value = {userInfo.password} placeholder="Enter Password" name="password" onChange= {updateStatus} required />
        <button type="submit" className="btn">Login</button>
        <div style={{ display: 'flex', alignItems: 'center' }}>
            <span style={{ marginRight: '8px' }}>Remember me?</span>
            <Form.Check
              type="checkbox"
              id="save-user"
              name="save-user"
              checked = {remember}
              onChange={handleRemember}
            />
          </div>
        <button style = {{marginTop: "10px"}} type="button" className="btn cancel" onClick={closeForm}>Close</button>
      </form>
    </div>
  );
};
