import React, { useState, useEffect } from 'react'; 
import userProfile from './Images/user-profile.png';
import { LoginForm } from './LoginForm';

export const HomePage: React.FC = () => {
  const [isFormOpen, setIsFormOpen] = useState<boolean>(false);
  const [userInfo, setInfo] = useState<{ username: string; password: string }>({ username: "", password: "" });
  const [remember, setRemember] = useState<boolean>(false);
  const [isLoggedIn, setIsLoggedIn] = useState<boolean>(false);
  const [formTitle, setFormTitle] = useState<string>("Create Account");

  useEffect(() => {
    const indexedDB = window.indexedDB;
    const request = indexedDB.open("UserDatabase", 1);
  
    request.onsuccess = () => {
      const db = request.result;
      const transaction = db.transaction("users", "readonly");
      const store = transaction.objectStore("users");
  
      // Use index to search by `username_and_password`
      const userQuery = store.index("username_and_password").get([userInfo.username, userInfo.password]);
  
      userQuery.onsuccess = () => {
        if (userQuery.result) {
          setFormTitle("Log In");
        } else {
          setFormTitle("Create Account");
        }
      };
  
      transaction.oncomplete = () => {
        db.close();
      };
    };
  }, [userInfo.username, userInfo.password]); // Check each time username or password changes
  

  const saveUser = () => {
    const indexedDB = window.indexedDB;
    const request = indexedDB.open("UserDatabase", 1);
  
    request.onsuccess = () => {
      const db = request.result;
      const transaction = db.transaction("users", "readwrite");
      const store = transaction.objectStore("users");
  
      // Just pass the userInfo object directly without an `id` field
      const addRequest = store.add(userInfo);
  
      addRequest.onsuccess = () => {
        console.log("User added to database with auto-generated ID!");
      };
  
      addRequest.onerror = (event) => {
        console.error("Error adding user to database:", event);
      };
  
      transaction.oncomplete = () => {
        db.close();
      };
    };
  };

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    saveUser();  // Save user data to IndexedDB
    setIsLoggedIn(true);
  };

  const toggleForm = () => {
    setIsFormOpen(!isFormOpen);
  };

  const updateStatus = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    setInfo((prevInfo) => ({
      ...prevInfo,
      [name]: value,
    }));
  };

  const handleRemember = () => {
    setRemember(!remember);
  };

  return (
    <div>
      {isLoggedIn ? (
        <div style={{ position: "absolute", float: "left" }}>
          <h3>Signed in as: {userInfo.username}!</h3>
        </div>
      ) : (
        <div>
          {/* Show the user image only when not logged in */}
          <img
            src={userProfile}
            alt="User Profile"
            style={{ float: "left", width: '50px', height: '55px', cursor: 'pointer' }}
            onClick={toggleForm}
          />
        </div>
      )}
      <a href="https://bleaky11.github.io/starter_helpi/" style={{ color: 'black' }}>
        <h1>The Career Quiz</h1>
      </a>

      {/* Conditionally render the LoginForm */}
      {isFormOpen && !isLoggedIn && (
        <LoginForm
          userInfo={userInfo}
          setInfo={setInfo}
          remember={remember}
          setRemember={setRemember}
          handleRemember={handleRemember}
          handleSubmit={handleSubmit}
          updateStatus={updateStatus}
          closeForm={() => setIsFormOpen(false)}
          formTitle={formTitle}
        />
      )}
    </div>
  );
};
