import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import { Button, Form } from 'react-bootstrap';
import { HomePage } from './homepagelogo';
import { BasicCareerComponent } from './basicCareer';

// local storage and API Key
let keyData = "";
const saveKeyData = "MYKEY";
const prevKey = localStorage.getItem(saveKeyData);
if (prevKey !== null) {
  keyData = JSON.parse(prevKey);
}

interface BasicCareer {
  setVisible: (newVisibility: boolean) => void;
  visible: boolean;
}

function SwitchPage({ setVisible, visible }: BasicCareer): JSX.Element {
  const handleClick = () => {
    setVisible(!visible); // Toggle the visibility
  };

  return (
    <div>
      <Button onClick={handleClick}>
        {visible ? "Go Back to Home" : "Basic Career Page"}
      </Button>
    </div>
  );
}

function App() {
  const [key, setKey] = useState<string>(keyData);
  const [visible, setVisible] = useState<boolean>(false);

  function handleSubmit() {
    localStorage.setItem(saveKeyData, JSON.stringify(key));
    window.location.reload();
  }

  function changeKey(event: React.ChangeEvent<HTMLInputElement>) {
    setKey(event.target.value);
  }

  return (
    <div>
      {visible ? (
        // Pass setVisible and visible as props to BasicCareerComponent
        <BasicCareerComponent setVisible={setVisible} visible={visible} />
      ) : (
        <div className="App">
          <header className="App-header">
            {/* Pass setVisible and visible to SwitchPage */}
            <SwitchPage setVisible={setVisible} visible={visible} />
            <HomePage />
            <img src={logo} className="App-logo" alt="logo" />
            <p>
              Edit <code>src/App.tsx</code> and save to reload.
            </p>
            <a
              className="App-link"
              href="https://reactjs.org"
              target="_blank"
              rel="noopener noreferrer"
            >
              Learn React | Ryan Burtson | Levi Chen | Thomas Florio | Tolu AKin
            </a>
          </header>
          <Form>
            <Form.Label>API Key:</Form.Label>
            <Form.Control
              type="password"
              placeholder="Insert API Key Here"
              onChange={changeKey}
            ></Form.Control>
            <br />
            <Button className="Submit-Button" onClick={handleSubmit}>
              Submit
            </Button>
          </Form>
        </div>
      )}
    </div>
  );
}

export default App;
