import React from 'react';

interface LoginFormProps {
  userInfo: { username: string; password: string };
  setUserInfo: React.Dispatch<React.SetStateAction<{ username: string; password: string }>>;
  remember: boolean;
  setRemember: React.Dispatch<React.SetStateAction<boolean>>;
  handleRemember: () => void;
  handleSubmit: (event: React.FormEvent<HTMLFormElement>) => void;
  updateStatus: (event: React.ChangeEvent<HTMLInputElement>) => void;
  accounts: { username: string; password: string }[]; // Expecting an array of account objects
  closeForm: () => void;
  formTitle: string;
}

export const LoginForm: React.FC<LoginFormProps> = ({
  userInfo,
  setUserInfo,
  remember,
  setRemember,
  handleRemember,
  handleSubmit,
  updateStatus,
  accounts,
  closeForm,
  formTitle,
}) => {
  const handleAccountSelect = (event: React.ChangeEvent<HTMLSelectElement>) => {
    const selectedAccount = accounts.find(account => account.username === event.target.value);
    if (selectedAccount) {
      setUserInfo({ username: selectedAccount.username, password: selectedAccount.password });
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>{formTitle}</h2>
      <select onChange={handleAccountSelect} value={userInfo.username}>
        <option value="">Select an account</option>
        {accounts.map((account, index) => (
          <option key={index} value={account.username}>{account.username}</option>
        ))}
      </select>
      <input
        type="text"
        name="username"
        value={userInfo.username}
        onChange={updateStatus}
        placeholder="Username"
        required
      />
      <input
        type="password"
        name="password"
        value={userInfo.password}
        onChange={updateStatus}
        placeholder="Password"
        required
      />
      <label>
        <input type="checkbox" checked={remember} onChange={handleRemember} />
        Remember Me
      </label>
      <button type="submit">{formTitle === "Create Account" ? "Sign Up" : "Log In"}</button>
      <button type="button" onClick={closeForm}>Close</button>
    </form>
  );
};
