import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import { Button, Form } from 'react-bootstrap';
import { HomePage } from './homepagelogo';
import { BasicCareerComponent } from './basicCareer';

//local storage and API Key: key should be entered in by the user and will be stored in local storage (NOT session storage)
let keyData = "";
const saveKeyData = "MYKEY";
const prevKey = localStorage.getItem(saveKeyData); //so it'll look like: MYKEY: <api_key_value here> in the local storage when you inspect
if (prevKey !== null) {
  keyData = JSON.parse(prevKey);
}

interface BasicCareer
{
    setVisible: (newVisibility: boolean) => void
    visible: boolean
}
  
function SwitchPage({ setVisible, visible }: BasicCareer): JSX.Element 
{
  const handleClick = () => {
      if (visible === false) 
      {
        <BasicCareerComponent></BasicCareerComponent>
      } else {
          setVisible(false); // Hide the page if it's currently visible
      }
  };

  return (
      <div>
          <Button onClick={handleClick}>
              Basic Career Page
          </Button>
      </div>
  );
}

function App() {
  const [key, setKey] = useState<string>(keyData); //for api key input
  const [visible, setVisible] = useState<boolean>(false); // visibilty for accessing basic questions

  //sets the local storage item to the api key the user inputed
  function handleSubmit() {
    localStorage.setItem(saveKeyData, JSON.stringify(key));
    window.location.reload(); //when making a mistake and changing the key again, I found that I have to reload the whole site before openai refreshes what it has stores for the local storage variable
  }

  //whenever there's a change it'll store the api key in a local state called key but it won't be set in the local storage until the user clicks the submit button
  function changeKey(event: React.ChangeEvent<HTMLInputElement>) {
    setKey(event.target.value);
  }
  return (
    <div className="App">
      <header className="App-header">
        <HomePage></HomePage>
        <SwitchPage setVisible={setVisible} visible={visible}/>
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.tsx</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React | Ryan Burtson | Levi Chen | Thomas Florio | Tolu AKin
        </a>
      </header>
      <Form>
        <Form.Label>API Key:</Form.Label>
        <Form.Control type="password" placeholder="Insert API Key Here" onChange={changeKey}></Form.Control>
        <br></br>
        <Button className="Submit-Button" onClick={handleSubmit}>Submit</Button>
      </Form>
    </div>
  );
}

export default App;
