import React, { useState, useEffect } from 'react';
import userProfile from './Images/user-profile.png';
import jerboa from './Images/Four-toes-jerboa-modified.png';
import { LoginForm } from './LoginForm';
import { useIndexedDB } from './useIndexedDB';
import { Button } from 'react-bootstrap';

export const HomePage: React.FC = () => {
  const [isFormOpen, setIsFormOpen] = useState<boolean>(false);
  const [userInfo, setUserInfo] = useState({ username: "", password: "" });
  const [remember, setRemember] = useState<boolean>(false);
  const [isLoggedIn, setIsLoggedIn] = useState<boolean>(false);
  const [formTitle, setFormTitle] = useState<string>("Create Account");
  const [savedUser, setSavedUser] = useState<string>("");

  const { addUser, getUser, getRememberedUser } = useIndexedDB();

  useEffect(() => {
    const loadRememberedUser = async () => {
      const rememberedUser = await getRememberedUser();
      if (rememberedUser) {
        setUserInfo({ username: rememberedUser.username, password: rememberedUser.password });
        setSavedUser(rememberedUser.username);
      }
    };
    loadRememberedUser();
  }, [getRememberedUser]);

  const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();

    const existingUser = await getUser(userInfo.username);
    if (existingUser) {
      if (existingUser.password === userInfo.password) {
        setIsLoggedIn(true);
      } else {
        alert("Incorrect password.");
      }
    } else if (formTitle === "Create Account") {
      addUser({ ...userInfo, remembered: remember });
      setIsLoggedIn(true);
    } else {
      alert("User not found. Please create an account.");
    }
  };

  const handleRemember = () => setRemember(!remember);

  const toggleForm = () => setIsFormOpen(!isFormOpen);

  const showForm = (title: string) => {
    setFormTitle(title);
    setIsFormOpen(true);
  };

  const handleLogOut = () => {
    setIsLoggedIn(false);
    setUserInfo({ username: "", password: "" });
    setRemember(false);
  };

  return (
    <div>
      {isLoggedIn ? (
        <div>
          <img src={jerboa} alt="Four-Toed Jerboa" />
          <Button onClick={handleLogOut}>Log out</Button>
        </div>
      ) : (
        <div>
          <img src={userProfile} alt="User Profile" onClick={() => showForm("Create Account")} />
          <Button onClick={() => showForm("Log in")}>Log in</Button>
        </div>
      )}

      {isFormOpen && !isLoggedIn && (
        <LoginForm
          userInfo={userInfo}
          setUserInfo={setUserInfo}
          remember={remember}
          setRemember={setRemember}
          handleRemember={handleRemember}
          handleSubmit={handleSubmit}
          formTitle={formTitle}
          closeForm={toggleForm}
          savedUser={savedUser}
          setSavedUser={setSavedUser}
          accounts={[{ username: userInfo.username, password: userInfo.password }]} // Pass in any existing user accounts here
        />
      )}

      <h1>The Career Quiz</h1>
    </div>
  );
};
