import React from 'react';
import { Form } from 'react-bootstrap';
import './LoginForm.css';

export interface LoginFormProps {
  closeForm: () => void;
  userInfo: { username: string; password: string };
  setUserInfo: (value: React.SetStateAction<{ username: string; password: string }>) => void;
  remember: boolean;
  setRemember: React.Dispatch<React.SetStateAction<boolean>>;
  updateStatus: (event: React.ChangeEvent<HTMLInputElement>) => void;
  handleRemember: () => void;
  handleSubmit: (event: React.FormEvent<HTMLFormElement>) => void; 
  formTitle: string;
  accounts: string[]; // Add accounts as a prop
  savedUser: string;
  setSavedUser: React.Dispatch<React.SetStateAction<string>>;
}

export const LoginForm: React.FC<LoginFormProps> = ({
  userInfo,
  setUserInfo,
  remember,
  setRemember,
  updateStatus,
  handleRemember,
  handleSubmit,
  closeForm,
  formTitle,
  accounts,
  savedUser,
  setSavedUser,
}) => {
  
  const handleAccountSelect = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setSavedUser(event.target.value);
  };

  return (
    <div className="form-popup" id="myForm">
      <form className="form-container" onSubmit={handleSubmit}>
        <h1>{formTitle}</h1>
        
        {/* Dropdown for Saved Users */}
        <Form.Group controlId="savedUsers">
          <Form.Label>Saved Usernames</Form.Label>
          <Form.Select value={savedUser} onChange={handleAccountSelect}>
            <option value="">Select a saved user</option>
            {accounts.map((user) => (
              <option key={user} value={user}>
                {user}
              </option>
            ))}
          </Form.Select>
        </Form.Group>

        <label htmlFor="username"><b>Username</b></label>
        <input
          type="text"
          value={userInfo.username}
          placeholder="Enter Username"
          name="username"
          onChange={updateStatus}
          required
        />
        
        <label htmlFor="psw"><b>Password</b></label>
        <input
          type="password"
          value={userInfo.password}
          placeholder="Enter Password"
          name="password"
          onChange={updateStatus}
          required
        />
        
        <button type="submit" className="btn">Login</button>
        
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <span style={{ marginRight: '8px' }}>Remember me?</span>
          <Form.Check
            type="checkbox"
            id="save-user"
            name="save-user"
            checked={remember}
            onChange={handleRemember}
          />
        </div>
        
        <button style={{ marginTop: "10px" }} type="button" className="btn cancel" onClick={closeForm}>Close</button>
      </form>
    </div>
  );
};
