import { backgroundStyle } from "./CSS/Background";
import { useState } from "react";
import { Container, Form } from "react-bootstrap";

export function BasicCareerComponent(): JSX.Element {
  // Define the questions array with an initial `isAnswered` flag set to false
  const questions = [
    { text: "Mock Question 1", isAnswered: false },
    { text: "Mock Question 2", isAnswered: false },
    { text: "Mock Question 3", isAnswered: false }
  ];

  // Initialize state to track progress and answered questions
  const [progress, setProgress] = useState<number>(0);
  const [answeredQuestions, setAnsweredQuestions] = useState<boolean[]>(new Array(questions.length).fill(false)); // Track answered state for each question

  // Update progress based on how many questions are answered
  function updateProgress(): void {
    const answeredCount = answeredQuestions.filter((isAnswered) => isAnswered).length;
    setProgress((answeredCount / questions.length) * 100);
  }

  // Handle when an answer is selected
  function handleAnswerChange(index: number): void {
    const updatedAnswers = [...answeredQuestions];
    updatedAnswers[index] = true; // Mark the current question as answered
    setAnsweredQuestions(updatedAnswers); // Update the state
    updateProgress(); // Update progress based on answered questions
  }

  return (
    <div style={backgroundStyle}>
      <div>
        {/* Progress bar showing completion */}
        <div style={{ display: "flex", alignItems: "center", justifyContent: "flex-end", marginBottom: "10px", marginRight: "30px" }}>
          <label htmlFor="question" style={{ marginRight: "10px" }}>
            Percent Complete: {progress.toFixed(0)}%
          </label>
          <progress id="question" value={progress} max="100" />
        </div>
        {/* Title */}
        <h1 style={{ textAlign: "center" }}>Here is the Basic Career Page!</h1>
        <br />
        {/* Instructions */}
        <Container style={{ border: "2px solid red" }}>
          <p>
            This assessment is designed to determine an appropriate career path going forward. You will be asked a series of multiple choice questions. Answer each question to the best of your ability.
          </p>
        </Container>
        <div style={{ marginLeft: "30px" }}>
          <br />
          {/* Map over questions to display them */}
          {questions.map((question, index) => (
            <div key={index}>
              {/* Question Text */}
              <p>{question.text}</p>
              {/* Answer options */}
              <Form>
                <Form.Check
                  type="radio"
                  label="answer"
                  name={`quizAnswer${index}`} // Unique name per question
                  value="answer"
                  onChange={() => handleAnswerChange(index)} // Handle answer change
                />
              </Form>
              {/* Display feedback if the question has been answered */}
              {answeredQuestions[index] && <p>You answered this question!</p>}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
