import React, { useState, useEffect } from 'react'; 
import userProfile from './Images/user-profile.png';
import { LoginForm } from './LoginForm';

export const HomePage: React.FC = () => {
  const [isFormOpen, setIsFormOpen] = useState<boolean>(false);
  const [userInfo, setUserInfo] = useState<{ username: string; password: string }>({ username: "", password: "" });
  const [remember, setRemember] = useState<boolean>(false);
  const [isLoggedIn, setIsLoggedIn] = useState<boolean>(false);
  const [formTitle, setFormTitle] = useState<string>("Create Account");
  const [db, setDb] = useState<IDBDatabase | null>(null); // State to store the database instance

  const initializeDatabase = () => {
    const request = indexedDB.open("UserDatabase", 1);

    request.onerror = (event) => {
      console.error("Error accessing user database!", event);
    };

    request.onupgradeneeded = (event) => {
      const db = request.result;
      if (!db.objectStoreNames.contains("users")) {
        const store = db.createObjectStore("users", { autoIncrement: true });
        store.createIndex("userData", ["username", "password"], { unique: true });
      }
    };

    request.onsuccess = () => {
      const db = request.result;
      setDb(db);
    };
  };

  useEffect(() => {
    initializeDatabase();
  }, []);

  useEffect(() => {
    if (db && userInfo.username) {
      const transaction = db.transaction("users", "readonly");
      const store = transaction.objectStore("users");
      const userQuery = store.index("userData").get([userInfo.username, userInfo.password]);
       userQuery.onsuccess = () => {
      if (userQuery.result && userQuery.result.password === userInfo.password) {
          setFormTitle("Log In");
     } else {
            setFormTitle("Create Account");
          }
        };

        userQuery.onerror = () => {
          console.error("Error accessing user index");
        };
      } else {
        console.error("Username index does not exist in the store");
      }
    }
  ); // Ensure db is available before running

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();

    if (!db) return;

    const transaction = db.transaction("users", "readwrite");
    const store = transaction.objectStore("users");

    const userRecord = {
      username: userInfo.username,
      password: userInfo.password,
    };

    const putRequest = store.put(userRecord);
    putRequest.onsuccess = () => {
      console.log("User data added/updated successfully");
      setIsLoggedIn(true);
    };

    putRequest.onerror = () => {
      console.error("Error adding/updating user data");
    };
  };

  const toggleForm = () => {
    setIsFormOpen(!isFormOpen);
  };

  const updateStatus = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    setUserInfo((prevInfo) => ({
      ...prevInfo,
      [name]: value,
    }));
  };

  const handleRemember = () => {
    setRemember(!remember);
  };

  return (
    <div>
      {isLoggedIn ? (
        <div style={{ position: "absolute", float: "left" }}>
          <h3>Signed in as: {userInfo.username}!</h3>
        </div>
      ) : (
        <div>
          <img
            src={userProfile}
            alt="User Profile"
            style={{ float: "left", width: '50px', height: '55px', cursor: 'pointer' }}
            onClick={toggleForm}
          />
        </div>
      )}
      <a href="https://bleaky11.github.io/starter_helpi/" style={{ color: 'black' }}>
        <h1>The Career Quiz</h1>
      </a>

      {isFormOpen && !isLoggedIn && (
        <LoginForm
          userInfo={userInfo}
          setUserInfo={setUserInfo}
          remember={remember}
          setRemember={setRemember}
          handleRemember={handleRemember}
          handleSubmit={handleSubmit}
          updateStatus={updateStatus}
          closeForm={() => setIsFormOpen(false)}
          formTitle={formTitle}
        />
      )}
    </div>
  );
};
